             bet: 10,
            points : 100
        }
    }
    componentDidMount(){
        this.get_new_deck();
        this.draw_a_card();
        
    }
    get_new_deck(){
        fetch("https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1")
        .then(res=>res.json())
        .then((result) =>{
            //console.log(result.deck_id);
            this.setState({
                
                deck_id : result.deck_id
