import React from 'react';


class ListOfCards extends React.Component{
    constructor(){
        super();
        if(localStorage.getItem("deck_id")==null){
            this.state = {
                deck_id : null,
                passed_cards:[],
                 bet: 10,
                points : 100
            }
        }else{
            this.state = {
                deck_id : localStorage.getItem("deck_id"),
                passed_cards:[],
                 bet: 10,
                points : 100
            }
        }
    }
    componentDidMount(){
        this.get_new_deck();
        
        
    }
    get_new_deck(){
        fetch("https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1")
        .then(res=>res.json())
        .then((result) =>{
            //console.log(result.deck_id);
            this.setState({
                
                deck_id : result.deck_id
            })
            this.draw_a_card(result.deck_id);
        },
        (err)=>{
            console.log(err);
        })
        
    }
    draw_a_card(){
        let url  = "https://deckofcardsapi.com/api/deck/"+this.state.deck_id+"/draw/?count=1";
        fetch(url)
        .then(res => res.json())
        .then(result =>{
            console.log(result);
        },
        err =>{
            console.log(err);
        })
        
    }

    lower_bet (){
        //alert('Lower bet');
    }

    

    render(){
        return(
            <div>
                

                <button onClick={this.lower_bet}>Lower</button>
            </div>
        )
    }

}
export default ListOfCards;
